import React,{useState} from "react";
import './Donate';
import { list, getNextId } from './List'
function Donate({lists}){
  const [todos, setTodos] = useState(list);  
  const [newItemFields, setNewItemFields]=useState({
        charityname:'',
        email:'',
        amount:'',
        name:'',
        telNo:''
      })

      function handleFields(e){
        const{name,value}=e.target
        setNewItemFields(newItemFields => {
            return{
                ...newItemFields,
                [name]:value
            }})
      }
      function handleSubmit(e){
        e.preventDefault()
         setNewItemFields({
            charityname:'',
            email:'',
            amount:'',
            name:'',
            telNo:''
        })
      }
    return (
      <div className="dona">
        <form onSubmit={handleSubmit}>
          <center>
            <input
            name="charityname"
            type="text"
            value={newItemFields.charityname}
            placeholder="Charity your donating to..."
            onChange={handleFields}/>
            <br></br>
             <input
            name='name'
            type="text"
            onChange={handleFields}
            value={newItemFields.name}
            placeholder="Your full name..."/>
            <br></br>
            <input
            name="email"
            type="email"
            value={newItemFields.email}
            placeholder="Email..."
            onChange={handleFields}/>
            <br></br>
            <input
            name='telNo'
            value={newItemFields.telNo}
            onChange={handleFields}
            placeholder="Telephone No. ..."
            type="number"/>
            <br></br>
            <input
            name="amount"
            value={newItemFields.amount}
            onChange={handleFields}
            type="number"
            placeholder="Amount your donating..."/>
            <div>
              <button id='donate' type="submit" class="btn btn-outline-dark">Submit</button>
            </div>
          </center>
        </form>
      </div>
    )
}
export default Donate
